import React, { Component } from "react";
class TodoListItem extends Component {
  constructor(props) {
    super(props);
    var content = this.props.todoListMsg;
    var id = this.props.id;
    this.state = {
      inputmsg: content,
      variable: true
    };
  }
  handleChange = event => {
    this.setState({
      inputmsg: event.target.value
    });
    console.log(this.state.inputmsg);
  };
  submit = () => {
    this.setState({
      variable: !this.state.variable
    });
  };
  inputfun = () => {
    var a = (
      <div>
        <input
          type="text"
          onChange={this.handleChange}
          value={this.state.inputmsg}
        />
        <button onClick={this.submit}> update</button>
      </div>
    );
    return a;
  };
  textfun = () => {
    var k = (
      <div>
        {this.state.inputmsg}
        <button onClick={this.submit}> edit</button>
      </div>
    );
    return k;
  };
  final = () => {
    this.props.temp(this.state.inputmsg);
  };

  render() {
    return (
      <div>
        {this.state.variable ? this.textfun() : this.inputfun()}
        {this.final()}
      </div>
    );
  }
}
export default TodoListItem;
