import React, { Component } from "react";
class TodoListItem extends Component {
  constructor(props) {
    super(props);
    var content = this.props.todoListMsg;
    //var id = this.props.todoListMsg.id;
    this.state = {
      inputmsg: content,
      variable: true
    };
  }
  handleChange = event => {
    this.setState({
      inputmsg: event.target.value
    });
    console.log(this.state.inputmsg);
  };
  submit = () => {
    this.setState({
      variable: false
    });
  };
  submit2 = () => {
    this.setState({
      variable: true
    });
    this.props.temp(this.state.inputmsg);
  };

  render() {
    return (
      <div>
        <input
          type="text"
          onChange={this.handleChange}
          value={this.state.inputmsg}
          disabled={this.state.variable}
        />
        <button onClick={this.submit}>edit</button>
        <button onClick={this.submit2}>submit</button>
      </div>
    );
  }
}
export default TodoListItem;
