import React, { Component } from "react";
class TodoListItem extends Component {
  constructor(props) {
    super(props);
    var content = this.props.todoListMsg;
    //var id = this.props.todoListMsg.id;
    this.state = {
      inputmsg: content,
      variable: true
    };
  }
  handleChange = event => {
    this.setState({
      inputmsg: event.target.value
    });
    console.log(this.state.inputmsg);
  };
  submit = () => {
    this.setState({
      variable: !this.state.variable
    });
  };

  render() {
    var a = (
      <div>
        <input
          type="text"
          onChange={this.handleChange}
          value={this.state.inputmsg}
          onFocusOut={this.submit}
        />
        <button onClick={this.submit}>edit</button>
      </div>
    );

    var b = this.state.inputmsg;

    return (
      <div>
        {this.state.variable ? b : a}

        {}
      </div>
    );
  }
}
export default TodoListItem;
